const initialState = {
  name: "nuri",
  age: 26,
  password: "cheese",
};

const changeNickname = () => {
  return {
    type: "CHANGE_NICKNAME",
    data: "kim nuri",
  };
};
const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case "CHANGE_NICKNAME":
      return {
        ...state,
        name: action.data,
      };
  }
};

export default rootReducer;
