import React from "react";
import { Card } from "antd";
import {
  RetweetOutlined,
  HeartOutlined,
  MessageOutlined,
  EllipsisOutlined,
} from "@ant-design/icons";

const PostCard = ({ post }) => {
  return (
    <div>
      <Card
        cover={post.Images[0] && <PostImages iamges={post.Images} />}
        actions={[
          <>
            <RetweetOutlined />
            <HeartOutlined />
            <MessageOutlined />
            <Popover>
              <EllipsisOutlined />
            </Popover>
          </>,
        ]}
      >
        <Content />
      </Card>
      <CommentForm />
      <Comments />
    </div>
  );
};

export default PostCard;
