import React from "react";
import { Button, Card, Popover } from "antd";
import {
  RetweetOutlined,
  HeartOutlined,
  MessageOutlined,
  EllipsisOutlined,
} from "@ant-design/icons";

const PostCard = ({ post }) => {
  return (
    <div>
      <Card
        cover={post.Images[0] && <PostImages iamges={post.Images} />}
        actions={[
          <>
            <RetweetOutlined key="retweet" />
            <HeartOutlined key="heart" />
            <MessageOutlined key="comment" />
            <Popover
              key="more"
              content={
                <Button.Group>
                  <Button>수정</Button>
                  <Button>삭제</Button>
                  <Button>신고</Button>
                </Button.Group>
              }
            >
              <EllipsisOutlined />
            </Popover>
          </>,
        ]}
      >
        <Content />
      </Card>
      <CommentForm />
      <Comments />
    </div>
  );
};

export default PostCard;
