import {
  all,
  fork,
  call,
  put,
  take,
  takeEvery,
  takeLatest,
  delay,
} from "redux-saga/effects";
import axios from "axios";

// 예외적으로 generator 아님
function logInAPI(data) {
  return axios.post("/api/login", data);
}
function* logIn(action) {
  try {
    // call : 동기 함수 호출 (결과를 기다렸다가 다음 실행)
    // fork : 비동기 함수 호출
    //const result = yield call(logInAPI, action.data);
    yield delay(1000);
    // put : dispatch 와 같은 역할, action을 dispatch 한다고 생각
    yield put({
      type: "LOG_IN_SUCCESS",
      data: result.data,
    });
  } catch {
    yield put({
      type: "LOG_IN_FAILURE",
      data: err.response.data,
    });
  }
}

function logOutAPI() {
  return axios.post("/api/logout");
}
function* logOut() {
  try {
    // const result = yield call(logOutAPI);
    yield delay(1000);
    yield put({
      type: "LOG_OUT_SUCCESS",
      data: result.data,
    });
  } catch {
    yield put({
      type: "LOG_OUT_FAILURE",
      data: err.response.data,
    });
  }
}

function addPostAPI(data) {
  return axios.post("/api/post", data);
}
function* addPost(action) {
  try {
    // const result = yield call(addPostAPI, action.data);
    yield delay(1000);
    yield put({
      type: "ADD_POST_SUCCESS",
      data: result.data,
    });
  } catch {
    yield put({
      type: "ADD_POST_FAILURE",
      data: err.response.data,
    });
  }
}

// EventListener 와 같은 역할
function* watchLogIn() {
  // take : action이 실행될 때 까지 기다림
  // take 가 일회성이라 while take 로 사용해야 함 -> 동기적으로 동작
  // while (true) {
  //   yield take("LOG_IN_REQUEST", logIn);
  // }
  //
  // takeEvery -> 비동기적으로 동작
  // yield takeEvery("LOG_IN_REQUEST", logIn);
  //
  // 버튼이 실수로 여러번 눌렸을 때 takeEvery 로 하면 두번 실행됨
  // 이 문제 방지 위해 사용하는 것이 takeLatest
  // takeLatest -> 앞의 클릭은 무시되고 마지막것만 실행함
  // front 에서 backEnd 로 보낸 여러 '요청'들은 취소 할 수 없고
  // BackEnd 로 부터 받는 '응답'은 마지막 하나를 제외하고 전부 취소함
  //
  // 여러 클릭 중에 첫번째만 실행하려면 takeLeading
  //
  // throttle -> 시간을 설정해두면 해당 시간동안에는 요청도 한번만 실행 할 수 있음
  yield takeLatest("LOG_IN_REQUEST", logIn);
}
function* watchLogOut() {
  yield takeLatest("LOG_OUT_REQUEST", logOut);
}
function* watchAddPost() {
  yield takeLatest("ADD_POST_REQUEST", addPost);
}

// rootSaga에 만들고 싶은 비동기 action들을 넣어줌
export default function* rootSaga() {
  // all : 배열을 받아서 해당 배열을 한번에 실행시킴
  yield all([
    fork(watchLogIn), // fork : 함수를 한번에 실행시킴
    fork(watchLogOut),
    fork(watchAddPost),
  ]);
}
