export const initialState = {
  mainPosts: [
    {
      id: 1,
      User: {
        id: 1,
        nickname: "nuri",
      },
      content: "첫번째 게시글 #hashtag #익스프레스",
      Images: [
        {
          src: "https://www.gilbut.co.kr/book/view?bookcode=BN003343",
        },
        {
          src: "https://www.gilbut.co.kr/book/view?bookcode=BN003336",
        },
        {
          src: "https://www.gilbut.co.kr/book/view?bookcode=BN003287",
        },
      ],
      Comments: [
        {
          User: {
            nickname: "jam",
          },
          content: "신간이 출시되었군용ㅎ",
        },
        {
          User: {
            nickname: "네로",
          },
          content: "Hiiiiii~",
        },
      ],
    },
  ],
  imagePaths: [],
  postAdded: false,
};

// action 객체 생성
const ADD_POST = "ADD_POST";
export const addPost = {
  type: ADD_POST,
};

const dummyPost = {
  id: 2,
  content: "더미데이터 입니다",
  User: {
    id: 1,
    nickname: "nuri",
  },
  Images: [],
  Comments: [],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_POST:
      return {
        ...state,
        mainPosts: [dummyPost, ...state.mainPosts],
      };
    default:
      return state;
  }
};

export default reducer;
