// Redux 에 initialState를 두면 이것을 다양한 컴포넌트에서 원할 때 사용 가능
export const initialState = {
  isLoggedIn: false,
  me: null,
  singUpData: {},
  loginData: {},
};

// 사용자 입력에 따라 동적으로 action을 생성하는 'action creator'
export const loginRequestAction = (data) => {
  return {
    type: "LOG_IN",
    data,
  };
};
export const logoutRequestAction = (data) => {
  return {
    type: "LOG_OUT",
  };
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case "LOG_IN_REQUEST":
      return {
        ...state,
        isLoggedIn: true,
        me: action.data,
      };
    case "LOG_OUT":
      return {
        ...state,
        isLoggedIn: false,
        me: null,
      };
    default:
      return state;
  }
};

export default reducer;
