import { HYDRATE } from "next-redux-wrapper";

// Redux 에 initialState를 두면 이것을 다양한 컴포넌트에서 원할 때 사용 가능
const initialState = {
  user: {
    isLoggedIn: false,
    user: null,
    singUpData: {},
    loginData: {},
  },
  post: {
    mainPosts: [],
  },
};

// 사용자 입력에 따라 동적으로 action을 생성하는 'action creator'
export const loginAction = (data) => {
  return {
    type: "LOG_IN",
    data,
  };
};
export const logoutAction = (data) => {
  return {
    type: "LOG_OUT",
  };
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case HYDRATE:
      console.log("HYDRATE", action);
      return { ...state, ...action.payload };
    case "LOG_IN":
      return {
        ...state,
        user: {
          ...state.user,
          isLoggedIn: true,
          user: action.data,
        },
      };
    case "LOG_OUT":
      return {
        ...state,
        user: {
          ...state.user,
          isLoggedIn: false,
          user: null,
        },
      };
    default:
      return state;
  }
};

export default rootReducer;
