import { all, fork, call, put, take } from "redux-saga/effects";
import axios from "axios";

// 예외적으로 generator 아님
function logInAPI() {
  return axios.post("/api/login");
}

function* logIn() {
  try {
    yield put({
      type: "LOG_IN_REQUEST",
    });
    const result = yield call(logInAPI);
    // put : dispatch 와 같은 역할, action을 dispatch 한다고 생각
    yield put({
      type: "LOG_IN_SUCCESS",
      data: result.data,
    });
  } catch {
    yield put({
      type: "LOG_IN_FAILURE",
      data: err.response.data,
    });
  }
}

function* watchLogIn() {
  yield take("LOG_IN_REQUEST", logIn); // take : action이 실행될 때 까지 기다림
}
function* watchLogOut() {
  yield take("LOG_OUT_REQUEST");
}
function* watchAddPost() {
  yield take("ADD_POST_REQUEST");
}

// rootSaga에 만들고 싶은 비동기 action들을 넣어줌
export default function* rootSaga() {
  // all : 배열을 받아서 해당 배열을 한번에 실행시킴
  yield all([
    fork(watchLogIn), // fork : 함수를 한번에 실행시킴
    fork(watchLogOut),
    fork(watchAddPost),
  ]);
}
