const initialState = {
  user: {
    isLoggedIn: false,
    user: null,
    singUpData: {},
    loginData: {},
  },
  post: {},
};

// 사용자 입력에 따라 동적으로 action을 생성하는 'action creator'
const changeNickname = (data) => {
  return {
    type: "CHANGE_NICKNAME",
    data,
  };
};

